DROP TABLE IF EXISTS {output_schema}.window_crash_raw;
CREATE TABLE {output_schema}.window_crash_raw AS (
    SELECT
        t1.id,
        t2.{crash_table_mode_col}_{crash_table_sev_col},
        COUNT(t2.*)
    FROM
        {output_schema}.{sliding_name} t1,
        {output_schema}.{crash_name} t2
    WHERE ST_DWITHIN(t1.geom, t2.{crash_table_geom}, {search_distance})
    GROUP BY
        t1.id,
        t2.{crash_table_mode_col}_{crash_table_sev_col}
    ORDER BY
        t1.id,
        t2.{crash_table_mode_col}_{crash_table_sev_col}
);