UPDATE {output_schema}.{short_name} t1
SET {update_expression}
FROM (
    SELECT
        t1.short_window_id,
        {max_expression}
    FROM
        {output_schema}.{short_name} t1,
        {output_schema}.{sliding_name} t2
    WHERE ST_DWITHIN(t1.geom, t2.geom, 1)
    AND {where_expression}
    GROUP BY t1.short_window_id
    ) t2
WHERE t1.short_window_id = t2.short_window_id
;
