CREATE TABLE {out_schema}.{out_table} AS (
    SELECT
        {id_column},
        {fclass_column}::TEXT AS fclass,
        {geom},
        NULL::INTEGER AS lanes,
        NULL::BOOLEAN AS marked_centerline,
        NULL::INTEGER AS speed,
        NULL::INTEGER AS effective_aadt,
        NULL::BOOLEAN AS parking,
        NULL::BOOLEAN AS low_parking,
        NULL::INTEGER AS parking_width,
        NULL::INTEGER AS bike_lane_width,
        NULL::BOOLEAN AS oneway, 
        NULL::TEXT separation_type, 
        NULL::INTEGER AS stress, 
        NULL::TEXT AS criteria, 
        NULL::TEXT AS condition
    FROM {in_schema}.{in_table}
    WHERE FALSE
);

ALTER TABLE {out_schema}.{out_table} ADD COLUMN stress_pkey SERIAL PRIMARY KEY; 

CREATE INDEX IF NOT EXISTS idx_stress_pkey
ON {out_schema}.{out_table} 
USING BTREE (stress_pkey);

ANALYZE {out_schema}.{out_table} ;
 
